INSERT INTO articles (title, created_at, updated_at, body) values ('Geography of the Democratic Republic of the Congo', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '[[Image:Congo Democratic Republic Map.jpg|thumb|300px|right|Map of the DR of the Congo]]
